mapper.select:
SELECT ptr.id AS id, ptr.serial_num AS serialNum, ptr.add_time AS addTime, ptr.trade_type AS tradeType, ptr.money AS money, ptr.trade_status AS tradeStatus, us.user_name AS userName FROM pay_trade_record ptr LEFT JOIN user us ON us.user_id = ptr.user_id WHERE 1 = 1
AND ptr.add_time >= #{startDate}
AND ptr.add_time <= #{endDate}
AND ptr.trade_status = #{tradeStatus}
AND ptr.trade_type = #{tradeType}
