mapper.select:
SELECT wr.id AS id, wr.serial_no AS serialNo, us.user_name AS userName, org.org_name AS orgName, pa.use_balance AS useBalance, pa.balance AS balance, wr.money AS money, wr.add_time AS addTime, wr.check_time AS checkTime, wr.check_status AS checkStatus FROM withdraw_record wr LEFT JOIN pay_account pa ON wr.user_id = pa.user_id LEFT JOIN user us ON wr.user_id = us.user_id LEFT JOIN organization org ON org.org_id = us.org_id WHERE 1 = 1
AND wr.check_status = #{checkStatus}
AND wr.add_time >= #{startDate}
AND wr.add_time <= #{endDate}
AND (org.org_name LIKE '%${orgName}%' OR us.user_name LIKE '%${orgName}%')
ORDER BY wr.add_time DESC
